import axios from 'axios';
import * as cheerio from 'cheerio';

async function ttImage(url) {
  try {
  let response = await axios('https://ttsave.app/download', {
        method: 'POST',
        data: {
          query: url,
          langage_id: "2"
          },
        headers: {
          "cookie": "_ga=GA1.1.2134200012.1719399150; _ga_1CPHGEZ2VQ=GS1.1.1719399149.1.1.1719400570.0.0.0; XSRF-%3D; ttsaveapp_%3D",
          "user-agent": "Mozilla/5.0 (Linux; Android 10; K) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/116.0.0.0 Mobile Safari/537.36"
           }
        })
       // console.log(response.data)
   let html = response.data
   let $ = await cheerio.load(html)
   let foto = []
   let img = $('img').slice(1).each((index, element) => {
        let links = $(element).attr('src')
        foto.push({links})
        })
       let result = {
          status: true,
          creator: 'nuyOfc',
          image: foto
         }
         console.log(result)
         return result
    } catch(error) {
     return error
     console.error(error)
    }
  return;
}

export { ttImage }